import React, { useEffect, useState } from 'react';
import './App.css'

function App() {
  const[todo,setTodo]=useState("");
  const[todos,setTodos]=useState(getLocalItems());
  const[editId,setEditId]=useState(0);

  //to get the items from the local storage
  const getLocalItems = () =>{
    let list = localStorage.getItem('lists')

    if(lists){
      return JSON.parse(localStorage.getItem('lists'));
    }
    else{
      return [];
    }
  }

  //To handle the Add function
  const handleSubmit = (e) => {
    e.preventDefault();

    //To handle the edit function
    if(editId)
    {
      const edittodo=todos.find((i) => i.id === editId);
      const updatedtodos=todos.map((t)=>
      t.id===edittodo.id
      ?(t={id:t.id,todo}):({id:t.id,todo:t.todo})
      )
      setTodos(updatedtodos);
      setEditId(0);
      setTodo("");
      return;
    };
    if(todo!==""){
      setTodos([{id:`${todo}-${Date.now()}`,todo},...todos]);
    }
    setTodo("");
  };

  //To handle the delete function
  const handleDelete = (id) => {
    const delTodo = todos.filter((to) => to.id !== id);
    setTodos([...delTodo]);
  };

  const handleEdit = (id) =>{
    const edittodo = todos.find((i) => i.id === id);
    setTodo(edittodo.todo);
    setEditId(id);
  }

  //to handle the local storage
  useEffect(() => {
    localStorage.setItem('lists',JSON.stringify(todos));
  },[todos]);


  return (
    <div className="App">
      <div className="container">
        <h1>TODO LIST APP</h1>
        <form className="todoForm" onSubmit={handleSubmit}>
          <input 
          type="text" 
          value={todo} 
          onChange={(e) => setTodo(e.target.value)} 
          placeholder="Enter the task"/>
          <button className="id1" type="submit">{editId?"Edit":"AddTask"}</button>
        </form>
        <ul className="alltodo">
          {todos.map((t) => (
            <li className="singletodo">
              <span className="todotext" key={t.id}>{t.todo}</span>
              <button className="id2" onClick={() => handleEdit(t.id)}>Edit</button>
              <button className="id3" onClick={() => handleDelete(t.id)}>Delete</button>
          </li> 
          ))}
        </ul>
     </div>
    </div>
  );
}

export default App;
